from selenium import webdriverfrom selenium.webdriver.chrome.options import Optionsfrom selenium.webdriver.chrome.service import Servicefrom webdriver_manager.chrome import ChromeDriverManagerfrom selenium.webdriver.common.by import Byfrom selenium.webdriver.support.ui import WebDriverWaitfrom selenium.webdriver.support import expected_conditions as ECimport randomop = Options()op.add_argument("--no-headless")s = Service(ChromeDriverManager().install())driver = webdriver.Chrome(options=op, service=s)# 1. Load a twitter page from a given url using Seleniumurl = "https://twitter.com/search?q=%23Bitcoin"driver.get(url)visited_pages = 0while visited_pages < 6:    # 2. Wait for some tweeter feeds to load. You may use WebDriverWait with EC.    try:        WebDriverWait(driver, 30).until(            EC.presence_of_element_located((By.XPATH,                                            "//*[@class='css-1dbjc4n r-1iusvr4 r-16y2uox r-1777fci r-kzbkwu']")))        # print("Exists")        # 3.Select a hashtag randomly from the twitter page.        hashtags = driver.find_elements(By.XPATH, "//a[contains(@href, '/hashtag/')]")        print("Hashtags:", hashtags)        random_hashtag = random.choice(hashtags)        # print("Random Tweet:", random_hashtag)        # 4. Visit the randomly selected hashtag's hyperlink from step 3 and go to step 1.        driver.get(random_hashtag.get_attribute("href"))        print(f"Visited page {visited_pages}: {driver.current_url}")        visited_pages += 1    except Exception as e:        print("Something went wrong")        print(e)        breakdriver.quit()